import unittest
import isilib
import os
import filecmp

class TestRecordCollection(unittest.TestCase):
    def setUp(self):
        recList =set()
        for i in manypapersList:
            rec = isilib.Record('PT J')
            recList.add(rec.__setstate__(i))
        self.RC = isilib.RecordCollection(recList)

    def test_iscollection(self):
        self.assertTrue(isinstance(self.RC, isilib.RecordCollection))
    def test_write(self):
        fileName = 'OnePaper2.isi'
        isilib.RecordCollection('tests/' + fileName).writeFile(fileName + '.tmp')
        self.assertTrue(filecmp.cmp('tests/' + fileName,fileName + '.tmp'))
        os.remove(fileName + '.tmp')


